import React, { useState, useEffect } from 'react'
import { StyleSheet, Text, View, Image, AsyncStorage } from 'react-native';
import { TouchableOpacity } from 'react-native-gesture-handler';
import axios from 'axios'

const DailyChallenge = ({ navigation }) => {

    const [dailyChallenge, setDailyChallenge] = React.useState()

    useEffect(() => {
        challenge()
    }, [])

    const challenge = async () => {
        const dailyChallengeVar = await AsyncStorage.getItem('dailyChallenge')
        setDailyChallenge(dailyChallengeVar)
    }

    return (
        <View style={styles.container}>
            <View style={styles.headerDiv}>
                <TouchableOpacity style={styles.backButton}
                onPress={() => console.log(dailyChallenge)}
                    // onPress={() => navigation.toggleDrawer()}
                >
                    <Image style={styles.covidIcon}
                        source={require('../covidIcon.png')}
                        resizeMode="cover"
                    >
                    </Image>
                </TouchableOpacity >
                <Text style={styles.headerText}>Daily Challenge</Text>
            </View>
            <View style={styles.bottomDiv}>
                <View style={styles.leftDiv}>
                    <Text>{dailyChallenge.title}</Text>
                </View>
                <View style={styles.rightDiv}>

                </View>
            </View>
            <View style={styles.footer}>

            </View>
        </View>


    )
}

const styles = StyleSheet.create({
    container: {
        flexGrow: 1,
        borderTopWidth: 4,
        borderColor: "black",
        width: "100%",
        height: "100%",
    },
    headerDiv: {
        flex: 1,
        flexDirection: "row",
        width: "100%",
        maxHeight: "20%",
        padding: "1%",
        justifyContent: "center",

    },
    bottomDiv: {
        flex: 1,
        maxHeight: "65%",
        flexDirection: "row"
    },
    covidIcon: {
        maxHeight: "100%",
        width: "110%",
        justifyContent: "center",
    },
    backButton: {
        minWidth: "25%",
        maxHeight: "100%",
        justifyContent: "center"
    },
    headerText: {
        width: "70%",
        fontSize: 35,
        textAlign: "center",
        marginLeft: "2%",
        alignSelf: "center",
        textDecorationLine: "underline"
    },
    leftDiv: {
        flex: 1,
        flexDirection: "column",
        width: "75%",
        // backgroundColor: "yellow",
        height: "100%"
    },
    rightDiv: {
        flex: 1,
        flexDirection: "column",
        maxWidth: "25%",
        height: "100%",
        backgroundColor: "black"
    },
    footer: {
        flex: 1,
        width: "100%",
        maxHeight: "15%",
        backgroundColor: "green"
    }
})

export default DailyChallenge
