import React, { useEffect, useRef } from 'react';
import { StyleSheet, Text, View, Image, ImageBackground } from 'react-native';
import { TouchableOpacity, ScrollView } from 'react-native-gesture-handler';
import axios from 'axios'
import ChallengeInstance from './ChallengeInstance'

const MainChallengePage = ({ navigation }) => {

    const [challenges, setChallenges] = React.useState([])

    useEffect(() => {
        axios.get('https://covid-see10.herokuapp.com/api/challenges/')
            .then(response => {
                setChallenges(response.data)
            })
    }, [])

    const refHook = useRef(false)
    const didMountRef = useRef(false)

    useEffect(() => {
        if (didMountRef.current) {
            axios.get('https://covid-see10.herokuapp.com/api/challenges/')
                .then(response => {
                    setChallenges(response.data)
                })
        } else didMountRef.current = true
    }, [])


    const challengeCard = challenges.map(challenge => {
        console.log(challenge)
        return <ChallengeInstance navigation={navigation} title={challenge.title} daily={challenge.daily} key={challenge.id} points={challenge.points} reps={challenge.reps} sport={challenge.sport} workoutType={challenge.workout_type} />
    })

    return (
        <ImageBackground
            style={styles.background}
            source={require("../mainBackground.png")}
        >
                <ScrollView>
            <View style={styles.container} >
                    <View style={styles.topContainer}>
                        <TouchableOpacity style={styles.profile}
                            onPress={() => navigation.toggleDrawer()}
                        >
                            <Image style={styles.hamburgerImage}
                                source={require('../hamburger.png')}
                                resizeMode="cover"
                            >
                            </Image>
                        </TouchableOpacity>
                        <TouchableOpacity style={styles.dailyChallengeButton} onPress={() => navigation.navigate('Daily Challenge')}>
                            <Text style={styles.dailyChallengeText}>DAILY CHALLENGE</Text>
                        </TouchableOpacity>
                    </View>
                    <View style={styles.allChallenges}>
                        {challengeCard}
                    </View>
            </View>
                </ScrollView>
        </ImageBackground>
    )
}

const styles = StyleSheet.create({

    container: {
        flex: 1,
        borderColor: "black",
        flexDirection: "column",
        alignItems: "center",
    },
    pop: {
        backgroundColor: "red"
    },
    topContainer: {
        flex: 1,
        // borderTopWidth: 6,
        flexDirection: "row",
        width: "96%",
        maxHeight: "15%",
        justifyContent: "space-between",
        padding: ".75%",
        overflow: "hidden",
        marginTop: "1%",
    },
    background: {
        width: '100%',
        height: '110%',
        opacity: 0.8
    },
    dailyChallengeButton: {
        minWidth: "80%",
        flex: 1,
        borderTopStartRadius: 5,
        borderBottomStartRadius: 5,
        borderTopRightRadius: 5,
        borderBottomRightRadius: 5,
        justifyContent: "center",
        backgroundColor: "red",
        // opacity: 0.7,

    },
    profile: {
        minWidth: "17%",
        borderWidth: 1,
        flex: 1,
        // justifyContent: "center",
        borderColor: "transparent",
        marginTop: "20%"

    },
    hamburgerImage: {
        maxHeight: "70%",
        width: "100%",
    },
    allChallenges: {
        flex: 1,
        width: "95%",
        height: "100%",
        borderWidth: 2,
        borderColor: "black",
        backgroundColor: "black",
        flexDirection: "column",
        padding: "1%",
        overflow: "scroll",

    },
    dailyChallengeText: {
        fontSize: 25,
        textAlign: "center",
    }
});

export default MainChallengePage
