        flex: 1,
        flexDirection: "row",
        // alignItems: "center", 
        width: "100%",
        maxHeight: "10%",
        backgroundColor: "white",
        justifyContent: "space-between",
        padding: "1%",
    },
    bottomDiv: {
        width: "100%",
        height: "90%",
        flex: 1,
        flexDirection: "row",
        backgroundColor: "white"
    },
    headerText: {
        width: "65%",
        justifyContent: "center",
        alignItems: "center"
    },
    covidIcon: {
        maxHeight: "100%",
        width: "100%",
    },
    backButton: {
        minWidth: "16%",
    },
    headerTextStyle2: {
        fontSize: 20,
        fontWeight: "bold"
    },
    profileImage: {
        width: "15%",
        height: "99%"
    },
    background: {
        width: '100%',
        height: '110%',

    },
    hamburger: {
        minWidth: "10%",
        height: "8%",
        marginLeft: "4%",
    },
    rightBottomDiv: {
        width: "85%",
        height: "100%",
        flex: 1,
        flexDirection: "column",
        alignItems: "center",
        marginLeft: "-2%"
        // paddingLeft: "5%"
    },
    inputs: {
        width: "80%",
        height: "5%",
        borderBottomWidth: 1,
        marginBottom: "1%",
        textAlign: "center",
        fontSize: 18,
