import React, { Component } from 'react';
import { StyleSheet, Text, View, ImageBackground } from 'react-native';
import { TextInput, TouchableOpacity } from 'react-native-gesture-handler';
import axios from 'axios'

class Home extends Component {

    state = {
        challengesArray: []
    }

    //  getChallenges = () => {
    //      axios.get('https://covid-see10.herokuapp.com/api/challenges/')
    //     //  .then(response => console.log(response.data))
    //      .then(response => {
    //          this.setState({challengesArray: response.data})
    //          this.props.navigation.navigate('Main Page',  { challengesArray: this.state.challengesArray })
    //         })
    //     //  .then(console.log(this.state.challengesArray))

    // }

    render() {

        return (
            <ImageBackground
                style={styles.background}
                source={require("../homeBackgroundd.jpg")}

            >
                <View style={styles.container}>
                    <View style={styles.welcomeContainer}>
                        <Text style={styles.welcome}>Welcome to the Covid-See10 Challenge</Text>
                        <TextInput editable style={styles.username} placeholder="Username"></TextInput>
                        <TextInput editable style={styles.password} placeholder="Password" secureTextEntry={true}></TextInput>
                    </View>
                    <View style={styles.authButtons}>
                        <TouchableOpacity style={styles.signUpButton}>
                            <Text style={styles.signUpText}>Sign-Up</Text>
                        </TouchableOpacity>
                        <TouchableOpacity style={styles.loginButton} onPress={() => this.props.navigation.navigate("Main Page")} >
                            <Text style={styles.loginText}>Login</Text>
                        </TouchableOpacity>
                    </View>
                </View>
            </ImageBackground>
        );
    }
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        alignItems: 'center',
        backgroundColor: "whitesmoke",
        borderTopWidth: 6,
        borderColor: "black"
    },
    welcomeContainer: {
        flex: 1,
        alignItems: 'center',
        justifyContent: "center",
        width: "100%",
        maxHeight: "65%",      
    },
    authButtons: {
        flex: 1,
        flexDirection: "row",
        justifyContent: "space-evenly",
        width: "100%",
        marginTop: "1.5%"
    },
    welcome: {
        fontSize: 30,
        textAlign: 'center',
        textDecorationLine: "underline",
        fontWeight: "bold",
        marginBottom: "15%"
        // color: "white"
    },
    username: {
        width: "70%",
        height: "9%",
        borderWidth: 1,
        textAlign: "center",
        marginBottom: "4%",
        marginTop: "10%",
        backgroundColor: "black",
        color: "white"
    },
    password: {
        width: "70%",
        height: "14%",
        borderWidth: 1,
        textAlign: "center",
        backgroundColor: "black",
        color: "white"
    },
    loginButton: {
        maxHeight: "15%",
        minWidth: "47%",
        borderWidth: 1,
        flex: 1,
        justifyContent: "center",
        marginLeft: "-20%",
        backgroundColor: "black",
    },
    loginText: {
        textAlign: "center",
        fontSize: 20,
        color: "white"
    },
    signUpButton: {
        maxHeight: "15%",
        minWidth: "48%",
        borderWidth: 1,
        flex: 1,
        justifyContent: "center",
        marginRight: "-20%",
        backgroundColor: "black"
    },
    signUpText: {
        textAlign: "center",
        fontSize: 20,
        color: "white"
    },
    background: {
        width: '100%',
        height: '110%',
        opacity: 0.8
    }
});

export default Home
