import React from 'react';
import { StyleSheet, Text, View, Image, ImageBackground} from 'react-native';
import { TouchableOpacity, TextInput } from 'react-native-gesture-handler';

const ProfilePage = ({navigation}) => {

        return (
            <ImageBackground
                style={styles.background}
                source={require("../mainBackground.png")}
            >

                    <View style={styles.container} >
                        <View style={styles.headerDiv}>
                            <TouchableOpacity style={styles.backButton} onPress={() => navigation.toggleDrawer()}>
                                <Image style={styles.covidIcon}
                                    source={require('../covidIcon.png')}
                                    resizeMode="cover"
                                >
                                </Image>
                            </TouchableOpacity >
                            <View style={styles.headerText}>
                                <Text style={styles.headerTextStyle}>My Profile</Text>
                                <Text style={styles.headerTextStyle2}>Miriam Grigsby</Text>
                            </View>
                            <Image
                                style={styles.profileImage}
                                source={require('../profileIcon.png')}
                                resizeMode="contain"
                            >
                            </Image>
                        </View>
                        <View style={styles.bottomDiv}>
                            <View style={styles.rightBottomDiv}>
                                <Text style={styles.text}>Username</Text>
                                <TextInput editable style={styles.inputs} placeholder="miriamgrigsby"></TextInput>
                                <Text style={styles.text}>First Name</Text>
                                <TextInput editable style={styles.inputs} placeholder="Miriam"></TextInput>
                                <Text style={styles.text}>Last Name</Text>
                                <TextInput editable style={styles.inputs} placeholder="Grigsby"></TextInput>
                                <Text style={styles.text}>Town/City</Text>
                                <TextInput editable style={styles.inputs} placeholder="Colorado Springs"></TextInput>
                                <Text style={styles.text}>Country</Text>
                                <TextInput editable style={styles.inputs} placeholder="United States"></TextInput>
                                <Text style={styles.text}>Age</Text>
                                <TextInput editable style={styles.inputs} placeholder="25"></TextInput>
                                <Text style={styles.text}>Gender</Text>
                                <TextInput editable style={styles.inputs} placeholder="Female"></TextInput>
                                <Text style={styles.text}>Bio</Text>
                                <TextInput editable style={styles.bio} placeholder="this be me bio"></TextInput>
                                <TouchableOpacity style={styles.updateButton}>
                                    <Text style={styles.inputs2} >Update</Text>
                                </TouchableOpacity>
                            </View>
                        </View>
                    </View>
            </ImageBackground> 
        );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        borderTopWidth: 4,
        borderColor: "black",
        flexDirection: "column",
        alignItems: "center",
        width: "100%",
        height: "100%",
    },
    headerDiv: {
        flex: 1,
        flexDirection: "row",
        // alignItems: "center", 
        width: "100%",
        maxHeight: "10%",
        backgroundColor: "white",
        justifyContent: "space-between",
        padding: "1%",
    },
    bottomDiv: {
        width: "100%",
        height: "90%",
        flex: 1,
        flexDirection: "row",
        backgroundColor: "white"
    },
    headerText: {
        width: "65%",
        justifyContent: "center",
        alignItems: "center"
    },
    covidIcon: {
        maxHeight: "100%",
        width: "100%",
    },
    backButton: {
        minWidth: "16%",
    },
    headerTextStyle: {
        fontSize: 15,
    },
    headerTextStyle2: {
        fontSize: 18,
        fontWeight: "bold"
    },
    profileImage: {
        width: "15%",
        height: "99%"
    },
    background: {
        width: '100%',
        height: '110%',

    },
    hamburger: {
        minWidth: "10%",
        height: "8%",
        marginLeft: "4%",
    },
    rightBottomDiv: {
        width: "85%",
        height: "100%",
        flex: 1,
        flexDirection: "column",
        alignItems: "center",
        marginLeft: "-2%"
        // paddingLeft: "5%"
    },
    inputs: {
        width: "80%",
        height: "5%",
        borderBottomWidth: 1,
        marginBottom: "1%",
        textAlign: "center",
        fontSize: 18,
    },
    inputs2: {
        // width: "90%",
        // height: "5%",
        marginBottom: "1%",
        textAlign: "center",
        fontSize: 20,
        color: "white"
    },

    bio: {
        width: "80%",
        height: "8%",
        borderWidth: 1,
        textAlign: "center",
        fontSize: 15
    },
    text: {
        fontSize: 15,
        padding: "0.5%",
        color: "black",
    },
    updateButton: {
        marginTop: "2%",
        minWidth: "60%",
        height: "30%",
        borderWidth: 1,
        // paddingTop: "1.5%",
        justifyContent: "center",
        backgroundColor: "black",
        opacity: 0.8,

    }
});


export default ProfilePage
