import React, {useState, useEffect} from 'react'
import { StyleSheet, Text, View, Image, AsyncStorage } from 'react-native';
import { TouchableOpacity } from 'react-native-gesture-handler';
import axios from 'axios'

const DailyChallenge = ({navigation}) => {

    const [dailyChallenge, setDailyChallenge] = React.useState()

    useEffect(() => {
       challenge()
    }, [])

    const challenge = async() => {
        const dailyChallengeVar = await AsyncStorage.getItem('dailyChallenge')
                setDailyChallenge(dailyChallengeVar)
    }

    return (
        <View style={styles.container}>
            <View style={styles.headerDiv}>
                <TouchableOpacity style={styles.backButton} 
                onPress={() => navigation.toggleDrawer()}
                >
                    <Image style={styles.covidIcon}
                        source={require('../covidIcon.png')}
                        resizeMode="cover"
                    >
                    </Image>
                </TouchableOpacity >
                <Text style={styles.headerText}> Yeehaw Mf</Text>          
            </View>
            <View style={styles.leftDiv}>

            </View>
            <View style={styles.rightDiv}>

            </View>
        </View>


    )
}

const styles = StyleSheet.create({
    container: {
        flexGrow: 1,
        borderopWidth: 4,
        borderColor: "black",
        flexDirection: "column",
        width: "100%",
        height: "100%",
    },
    headerDiv: {
        flex: 1,
        flexDirection: "row",
        width: "100%",
        maxHeight: "20%",
        padding: "1%",
        justifyContent: "center",
        backgroundColor: "red",

    },
    covidIcon: {
        maxHeight:"100%",
        width: "110%",
        justifyContent: "center", 
    },
    backButton: {
        minWidth: "25%",
        maxHeight: "100%",
        justifyContent: "center"
    },
    headerText: {
        width: "70%",
        fontSize: 25,
        textAlign: "center",
        marginLeft: "2%",
        alignSelf: "center"
    },
    leftDiv: {
        flex: 1,
        flexDirection: "column",
        width: "75%",
        backgroundColor: "white",
        maxHeight: "90%"
    },
    rightDiv: {
        flex: 1,
        flexDirection: "column",
        width: "30%",
        height: "100%"
    }
})

export default DailyChallenge
