import React, { useState, useEffect, useRef } from 'react'
import { StyleSheet, Text, View, Image, AsyncStorage, SectionList, ImageBackground } from 'react-native';
import { TouchableOpacity } from 'react-native-gesture-handler';
import axios from 'axios'

const DailyChallenge = ({ navigation }) => {

    const [dailyChallenge, setDailyChallenge] = React.useState()

    // useEffect(() => {
    //     challenge()
    // }, [])

    const refHook = useRef(false)
    const didMountRef = useRef(false)



    useEffect(() => {
        if (didMountRef.current) {
            challenge()
        } else didMountRef.current = true
    })

    const challenge = async () => {
        const dailyChallengeVar = await AsyncStorage.getItem('dailyChallenge')
        setDailyChallenge(JSON.parse(dailyChallengeVar))
    }

    return (
        <View style={styles.container}>
            <View style={styles.headerDiv}>
                <ImageBackground style={styles.background}>
                <TouchableOpacity style={styles.backButton}
                    onPress={() => console.log(dailyChallenge)}
                    // onPress={() => navigation.toggleDrawer()}
                >
                    <Image style={styles.covidIcon}
                        source={require('../covidIcon.png')}
                        resizeMode="cover"
                    >
                    </Image>
                </TouchableOpacity >
                </ImageBackground>
            </View>
            <View style={styles.bottomDiv}>
                <View style={styles.leftDiv}>
                    <Text  style={styles.title}>{dailyChallenge.title}</Text>
                    <Text style={styles.text}>Workout Type: {dailyChallenge.workout_type}</Text>
                    <Text style={styles.text2}>Spot: {dailyChallenge.sport}</Text>
                    <Text style={styles.text3}>Reps: {dailyChallenge.reps}</Text>
                    <Text style={styles.text4}>Points: {dailyChallenge.points}</Text>
                </View>
                <View style={styles.rightDiv}>

                </View>
            </View>
            <View style={styles.footer}>

            </View>
        </View>


    )
}

const styles = StyleSheet.create({
    container: {
        flexGrow: 1,
        borderTopWidth: 4,
        borderColor: "black",
        width: "100%",
        height: "100%",
    },
    text: {
        fontSize: 20,
    },
    text2: {
        fontSize: 20,
    },
    text3: {
        fontSize: 20,
    },
    text4: {
        fontSize: 20,
    },
    title: {
        alignSelf: "center",
        fontSize: 30,
        marginBottom: "5%",
        textDecorationLine: "underline"
    },
    background: {
        width: '100%',
        height: '110%',
        opacity: 0.8
    },
    headerDiv: {
        flex: 1,
        flexDirection: "row",
        width: "100%",
        maxHeight: "20%",
        padding: "2%",

    },
    bottomDiv: {
        flex: 1,
        maxHeight: "65%",
        flexDirection: "row"
    },
    covidIcon: {
        maxHeight: "100%",
        width: "110%",
        justifyContent: "center",
    },
    backButton: {
        minWidth: "25%",
        maxHeight: "100%",
        justifyContent: "center"
    },
    leftDiv: {
        flex: 1,
        flexDirection: "column",
        width: "75%",
        height: "100%",
        padding: "1%",
        alignItems: "center",
        justifyContent: "space-evenly"
    },
    rightDiv: {
        flex: 1,
        flexDirection: "column",
        maxWidth: "25%",
        height: "100%",
        backgroundColor: "black"
    },
    footer: {
        flex: 1,
        width: "100%",
        maxHeight: "15%",
        backgroundColor: "green"
    }
})

export default DailyChallenge
