import React from 'react'
import { StyleSheet, Text, View, Image, Form } from 'react-native';
import { TouchableOpacity, TextInput } from 'react-native-gesture-handler';

const NewChallenge = ({navigation}) => {

    const addNewChallenge = (event) => {
        axios.post('https://covid-see10.herokuapp.com/api/challenges/', {dailtitle: "See10ArcherPulls", workout_type: "archer pullups", reps: 10, sport: "calisthenics", points: 10 })
    }

    // dont forget daily field when sending post

    return (
        <View style={styles.container}>
            <View style={styles.headerDiv}>
                <TouchableOpacity style={styles.backButton} onPress={() => navigation.toggleDrawer()}>
                    <Image style={styles.covidIcon}
                        source={require('../covidIcon.png')}
                        resizeMode="contain"
                    >
                    </Image>
                </TouchableOpacity > 
                <Text style={styles.headerText}>Add a New Challenge</Text>         
            </View>
            <View style={styles.bottomDiv}>
                <Text style={styles.text}>Title</Text>
                <TextInput editable style={styles.inputs} ></TextInput>
                <Text style={styles.text}>Workout Type</Text>
                <TextInput editable style={styles.inputs} ></TextInput>
                <Text style={styles.text}>Reps</Text>
                <TextInput editable style={styles.inputs} ></TextInput>
                <Text style={styles.text}>Sport</Text>
                <TextInput editable style={styles.inputs} ></TextInput>
                <Text style={styles.text}>Points</Text>
                <TextInput editable style={styles.inputs}></TextInput>
                <TouchableOpacity style={styles.updateButton} onPress={addNewChallenge}>
                    <Text style={styles.inputs2} >Submit</Text>
                </TouchableOpacity>
            </View>
        </View>


    )
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        maxWidth: "100%",
        maxHeight: "100%",
        padding: ".75%",
        borderTopWidth: 4,
        // marginTop: "2%" 
        backgroundColor: "black",
        opacity: 0.8
    },
    headerDiv: {
        flex: 1,
        flexDirection: "row",
        maxWidth: "100%",
        maxHeight: "15%",
        padding: ".75%",
        alignItems: "center",
        backgroundColor: "white"

    },
    text: {
        fontSize: 18,
        fontWeight: "bold",
         marginTop: "2%",
    },
    bottomDiv: {
        flex: 1,
        alignItems: "center",
        backgroundColor: "white",
        width: "100%",
        height: "100%",
    },
    backButton: {
        height: "100%",
        width: "30%",
        justifyContent: "center",
        alignItems: "center"
    },
    covidIcon: {
        height: "100%",
        minWidth: "10%",
    },
    headerText: {
        marginLeft: "-40%",
        fontSize: 25,
        textDecorationLine: "underline",
    },
    inputs: {
        fontSize: 18,
        borderWidth: 6,
        borderColor: "black",
        minHeight: "8%",
        width: "80%",
        textAlign: "center",
        backgroundColor: "#800020"
    },
    updateButton: {
        marginTop: "2%",
        borderWidth: 2,
        minWidth: "30%",
        minHeight: "10%",
        alignItems: "center",
        justifyContent: "center"
    },
    inputs2: {
        fontSize: 20,
        fontWeight: "bold"
    }
})

export default NewChallenge
